import React from 'react';
import Calculator from "./Calculator"
import io from 'socket.io-client';
const socket = io("http://localhost:4000");

class SharedPanel extends React.Component {
  state = {calc: "", accumulatedCalc: []};
  componentDidMount(){
    // socket.on("messageFromServer", (value)=>{
    //   let arrCalc = [...this.state.accumulatedCalc, value];
    //   if(arrCalc.length > 10){
    //     arrCalc.splice(0,1);
    //   }
    //   this.setState({"calc":value, accumulatedCalc: arrCalc});
    // });
  }

  componentWillUnmount(){
    socket.emit("disconnect");
  }

  onInputChange = (event) => {
    socket.emit("sendMessageToServer", event.target.value);
  }

  displayAllCalculations = () =>{
    return this.state.accumulatedCalc.map((item)=>{
      return <div key={item}>{item}</div>
    })
  }

  render(){

    return (
      <div>
        Hi React App
        <p><input type="text" name="" value={this.state.calc} onChange={this.onInputChange}/></p>
        <div >
          {this.displayAllCalculations()}
        </div>

      </div>

    )
  }
}

export default SharedPanel;
