      <div className="container">
        <div className="row">
          <div className="col align-self-center">
            <Calculator emitEvent={this.emitSocketEvent}></Calculator>
          </div>
          <div className="col align-self-center">
            <SharedPanel></SharedPanel>
          </div>
        </div>
      </div>

    )
  }
}

export default App;
