import React from 'react';
import io from 'socket.io-client';
const socket = io("http://localhost:4000");

class App extends React.Component {
  state = {calc: ""};
  componentDidMount(){
    socket.on("messageFromServer", (value)=>{
      this.setState({"calc":value});
    });
  }

  onInputChange = (event) => {
    socket.emit("sendMessageToServer", event.target.value);
  }

  render(){

    return (
      <div>
        Hi React App
        <p><input type="text" name="" value={this.state.calc} onChange={this.onInputChange}/></p>
      </div>

    )
  }
}

export default App;
